import{_ as e,c as a,o as i,b as t,d as o}from"./app.97c19779.js";const b=JSON.parse('{"title":"Evasion","description":"","frontmatter":{},"headers":[],"relativePath":"faq/33.md","lastUpdated":1670171088000}'),n={name:"faq/33.md"},s=t("h1",{id:"evasion",tabindex:"-1"},[o("Evasion "),t("a",{class:"header-anchor",href:"#evasion","aria-hidden":"true"},"#")],-1),r=t("p",null,"tags: evasion",-1),c=t("p",null,"Evasion Action is pressing the button. Some valks, like KMB and GK (HoV), can trigger their Evasion Skill with their evasion action without performing an ultimate evade.",-1),l=t("p",null,"Ultimate Evasion is using Evasion Action to dodge through an active hitbox. Specifically, the hitbox of the attack must come in contact with the Valkyrie's hurtbox. This distinction is important, as there are valks whose Evasion Actions can actually dodge hitboxes entirely, such as Roza's - if she outright avoids the attack by teleporting underneath it, it will not trigger an Ultimate Evasion.",-1),h=t("p",null,"If the Valkyrie instead puts up a shield, then depending on the mechanism for defending, there are variants of this for those Valkyries. Bronya performs Perfect Defense when you shield an attack then immediately drop the shield after the attack is blocked. BK has a unique Retaliation mechanic in addition to a standard dash-type evasion action.",-1),d=t("p",null,"Evasion Skill is an ability tied to evasion. For most valks, it activates automatically if it is not on CD and you perform an Ultimate Evasion or Perfect Defense.",-1),p=t("p",null,"Evadion is built by either performing Retaliation, Perfect Defense, or an Ultimate Evasion - however, Ultimate Evasions can only earn Evadion once per internal evasion cooldown for that character.",-1),v=[s,r,c,l,h,d,p];function f(u,m,_,g,k,y){return i(),a("div",null,v)}const x=e(n,[["render",f]]);export{b as __pageData,x as default};
