import{_ as e,c as o,o as a,a as t}from"./app.8a15cda7.js";const f=JSON.parse('{"title":"How to clear cache on PC","description":"","frontmatter":{},"headers":[],"relativePath":"faq/54.md","lastUpdated":1670208811000}'),c={name:"faq/54.md"},n=t('<h1 id="how-to-clear-cache-on-pc" tabindex="-1">How to clear cache on PC <a class="header-anchor" href="#how-to-clear-cache-on-pc" aria-hidden="true">#</a></h1><p>tags: pcclearcache</p><ol><li>Please, close both game and launcher.</li><li>Press Windows + R, input regedit.</li><li>In the opened window, please locate <code>\\HKEY_CURRENT_USER\\Software\\miHoYo\\Honkai Impact 3</code> and delete it.</li><li>Press Windows + R, and input <code>%userprofile%\\Appdata\\LocalLow\\miHoYo\\</code> in the opened window. Delete all the files in the Honkai Impact 3/3rd folder.</li><li>Restart your PC.</li></ol><p><strong>Do note that doing this will require you to login again. Linking account is highly recommended.</strong></p><p>*also refer to system requirements: <a href="/honkaifaq/faq/11"><code>!faq pc spec</code></a></p><p><img src="https://cdn.discordapp.com/attachments/427673685026013184/928289694654025828/unknown-4-1.png" alt="notebook"><img src="https://cdn.discordapp.com/attachments/427673685026013184/928289694930858024/unknown-5-1.png" alt="pc"></p>',6),i=[n];function r(s,d,l,p,h,_){return a(),o("div",null,i)}const u=e(c,[["render",r]]);export{f as __pageData,u as default};
