import{_ as e,c as o,o as t,d as a}from"./app.f6e5a2c2.js";const f=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"How to clear cache on PC","slug":"how-to-clear-cache-on-pc","link":"#how-to-clear-cache-on-pc","children":[]}],"relativePath":"faq/54.md","lastUpdated":1670084221000}'),c={name:"faq/54.md"},n=a('<h2 id="how-to-clear-cache-on-pc" tabindex="-1">How to clear cache on PC <a class="header-anchor" href="#how-to-clear-cache-on-pc" aria-hidden="true">#</a></h2><p>tags: pcclearcache</p><ol><li>Please, close both game and launcher.</li><li>Press Windows + R, input regedit.</li><li>In the opened window, please locate <code>\\HKEY_CURRENT_USER\\Software\\miHoYo\\Honkai Impact 3</code> and delete it.</li><li>Press Windows + R, and input <code>%userprofile%\\Appdata\\LocalLow\\miHoYo\\</code> in the opened window. Delete all the files in the Honkai Impact 3/3rd folder.</li><li>Restart your PC.</li></ol><p><strong>Do note that doing this will require you to login again. Linking account is highly recommended.</strong></p><p>*also refer to system requirements: <a href="https://discord.com/channels/418652140454674432/427673685026013184/928289696122011668" target="_blank" rel="noreferrer">https://discord.com/channels/418652140454674432/427673685026013184/928289696122011668</a></p>',5),r=[n];function i(l,s,d,h,p,_){return t(),o("div",null,r)}const u=e(c,[["render",i]]);export{f as __pageData,u as default};
